}

void maxmin(int a[],int len,int *max,int *min){
    int i;
    *max = *min = a[0];
    for(i=0;i<len;i++){
        if(a[i]<*min){
            *min = a[i];
        }
        if(a[i]>*max){
            *max = a[i];
        }
    }
}
