#include<stdio.h>

int main(void){
    int n,k;
    scanf("%d %d",&n,&k);
    long long matrix[7][7],res[7][7];
    for(int i = 0;i<n;i++){
        for(int j = 0;j<n;j++){
            scanf("%d",&matrix[i][j]);
            res[i][j] = matrix[i][j];
        }
    }
    long long temp[7][7];
    if(k == 1){
        for(int i = 0;i<n;i++){
            for(int j = 0;j<n;j++){
                printf("%lld ",res[i][j]);
            }
            printf("\n");
        }
        return 0;
    }
    for(int step = 0;step<k-1;step++){
        for(int i = 0;i<n;i++){
            for(int j = 0;j<n;j++){
                temp[i][j] = 0;
            }
        }

        for(int row = 0;row<n;row++){
            for(int col = 0;col<n;col++){
                long long sum = 0;
                for(int t=0;t<n;t++){
                    sum += res[row][t]*matrix[t][col];
                }
                temp[row][col] = sum;
            }
        }

        for(int i = 0;i<n;i++){
            for(int j = 0;j<n;j++){
                res[i][j] = temp[i][j];
            }
        }
    }
    for(int i = 0;i<n;i++){
        for(int j = 0;j<n;j++){
            printf("%lld ",res[i][j]);
        }
        printf("\n");
    }

    return 0;
}
```

