        }
    } else if (v1) {
        // åªæœ‰ MM/DD/YY åˆæ³•
        printf("%s %d, %d\n", months[m1], d1, y1);
    } else if (v2) {
        // åªæœ‰ YY/MM/DD åˆæ³•
        printf("%s %d, %d\n", months[m2], d2, y2);
    }

    return 0;
}
