    for(int i = 0;i<n;i++){
        cin >> nums[i];
    }

    vector<int> res = nums;
    int half = w/2;
    for(int i = 0;i<n;i++){
        int left = i - half;
        int right = i + half;

        if(left < 0 ||  right >= n){
            continue;
