#include<bits/stdc++.h>
using namespace std;

int is_palindrome(const string &s){
    if(s.length() < 2 || s[0] == '0') return 0;
    int i = 0,j = s.length() - 1;
    while(i < j){
        if(s[i++] != s[j--]) return 0;
    }
    return 1;
}

int cmp_num(const string &a,const string &b){
    if(a.length() != b.length()) return a.length() < b.length();
    return a<b;
}

int main(void){
    string line;
    map<string,int> cnts;

    while(getline(cin,line)){
        string curr_digit = "";
        for(int i = 0;i<=line.length();i++){
            if(i<line.length() && isdigit(line[i])){
                curr_digit += line[i];
            }else{
                if(!curr_digit.empty()){
                    if(is_palindrome(curr_digit)){
                        cnts[curr_digit] ++;
                    }
                    curr_digit = "";
                }
            }
        }
    }

    if(cnts.empty()){
        cout << "None" << '\n';
        return 0;
    }

    int max_freq = 0;
    for(auto const& [num,freq] : cnts){
        if(freq > max_freq) max_freq = freq;
    }

    vector<string> result;
    for(auto const&[num,freq]: cnts){
        if(freq == max_freq){
            result.push_back(num);
        }
    }

    sort(result.begin(),result.end(),cmp_num);

    for(const string& s : result){
        cout << s << " " << max_freq << '\n';
    }

    return 0;
}
