```cpp
#include <vector>
#include <string>
#include <algorithm>
#include <map>

using namespace std;

// æ—¶é—´è½¬æ¢å‡½æ•°ï¼šå°† hh:mm è½¬ä¸ºç›¸å¯¹äº 09:00 çš„åˆ†é’Ÿæ•°
int toMin(string s) {
    int h = stoi(s.substr(0, 2));
    int m = stoi(s.substr(3, 2));
    return (h - 9) * 60 + m;
}

struct Team {
    int id;
    string name;
    int solved = 0;
    int penalty = 0;
    int rank = 0;
};

struct Submission {
    int tid, pid, time;
    string status;
};

struct Node {
    bool is_ac = false;
    int fail_count = 0;
    int ac_time = 0;
};

int main() {
    int n, m, q;
    cin >> n >> m >> q;

    vector<Team> teams(m + 1);
    for (int i = 0; i < m; ++i) {
        int tid;
        string name;
        cin >> tid >> name;
        teams[tid].id = tid;
        teams[tid].name = name;
    }

    vector<Submission> subs(n);
    for (int i = 0; i < n; ++i) {
        string t_str;
        cin >> subs[i].tid >> subs[i].pid >> subs[i].status >> t_str;
        subs[i].time = toMin(t_str);
    }

    while (q--) {
        int type;
        string query_t_str;
        cin >> type >> query_t_str;
        int query_t = toMin(query_t_str);

        // ç»Ÿè®¡ query_t æ—¶åˆ»å‰æ‰€æœ‰é˜Ÿä¼çš„çŠ¶æ€
        vector<vector<Node>> stat(m + 1, vector<Node>(11));
        for (int i = 0; i < n; ++i) {
            if (subs[i].time > query_t) continue;
            int tid = subs[i].tid;
            int pid = subs[i].pid;
            if (stat[tid][pid].is_ac) continue; // ACåä¸å†ç»Ÿè®¡

            if (subs[i].status == "AC") {
                stat[tid][pid].is_ac = true;
                stat[tid][pid].ac_time = subs[i].time;
            } else if (subs[i].status != "CE") {
                stat[tid][pid].fail_count++;
            }
        }

        // è®¡ç®—ç½šæ—¶å’Œè§£é¢˜æ•°
        vector<Team> rank_list;
        for (int i = 1; i <= m; ++i) {
            Team t = teams[i];
            t.solved = 0;
            t.penalty = 0;
            for (int p = 1; p <= 10; ++p) {
                if (stat[i][p].is_ac) {
                    t.solved++;
                    t.penalty += stat[i][p].ac_time + stat[i][p].fail_count * 20;
                }
            }
            rank_list.push_back(t);
        }

        // æ’åºè§„åˆ™
        sort(rank_list.begin(), rank_list.end(), [](const Team& a, const Team& b) {
            if (a.solved != b.solved) return a.solved > b.solved;
            if (a.penalty != b.penalty) return a.penalty < b.penalty;
            return a.id < b.id;
        });

        // æ ‡è®°æ’å
        for (int i = 0; i < m; ++i) {
            rank_list[i].rank = i + 1;
        }

        // å¤„ç†æŸ¥è¯¢
        if (type == 0) {
            int q_tid;
            cin >> q_tid;
            for (auto& t : rank_list) {
                if (t.id == q_tid) {
                    cout << t.rank << " " << t.id << " " << t.name << " " << t.solved << " " << t.penalty << endl;
                    break;
                }
            }
        } else {
            string q_name;
            cin >> q_name;
            for (auto& t : rank_list) {
                if (t.name == q_name) {
                    cout << t.rank << " " << t.id << " " << t.name << " " << t.solved << " " << t.penalty << endl;
                }
            }
        }
    }

    return 0;
}
```

