for(i=0;i<size && result ==-1;i++){
	numOfO = numOfX = 0;
	for(j=0;j<size;j++){
		if(board[i][j] == 1){
			numOfX ++;
		}else{
			numOfO ++;
		}
	}
	if(numOfO == size){
		result = 0;
	}else if(numOfX == size){
		result = 1;
	}
}
//检查列
if(result == -1){
	for(j=0;j<size && result == -1;j++){
		numOfO = numOfX = 0;
		for(i=0;i<size;i++){
			if(board[i][j] == 1){
				numOfX ++;
			}else{
				numOfO ++;
			}
		}
		if(numOfO == size){
			result = 0;
		}else if(numOfX == size){
			result = 1;
		}
	}
}
// 检查对角线（主对角线 + 副对角线）
if(result == -1){ // 仅当之前未检测到获胜者时才检查
    // 1. 检查主对角线（左上 → 右下：i == j，如 (0,0)、(1,1)、(2,2)）
    numOfO = numOfX = 0; // 重置计数
    for(i = 0; i < size; i++){
        if(board[i][i] == 1){ // 当前位置是X（用1表示）
            numOfX++;
        } else { // 当前位置是O（用非1表示，如0）
            numOfO++;
        }
    }
    // 判断主对角线是否获胜
    if(numOfO == size){
        result = 0; // O赢
    } else if(numOfX == size){
        result = 1; // X赢
    }

    // 2. 检查副对角线（右上 → 左下：i + j == size-1，如 (0,2)、(1,1)、(2,0)）
    if(result == -1){ // 主对角线未分胜负时，再检查副对角线
        numOfO = numOfX = 0; // 重置计数
        for(i = 0; i < size; i++){
            // 副对角线列号 = size-1 - 行号（如i=0时j=2，i=1时j=1，i=2时j=0）
            if(board[i][size - 1 - i] == 1){
                numOfX++;
            } else {
                numOfO++;
            }
        }
        // 判断副对角线是否获胜
        if(numOfO == size){
            result = 0; // O赢
        } else if(numOfX == size){
            numOfX = size;
            result = 1; // X赢
        }
    }
}

// （可选）输出结果，验证逻辑正确性
if(result == 1){
    printf("X赢\n");
} else if(result == 0){
    printf("O赢\n");
} else {
    printf("没人赢\n");
}
```

